active-tab :> id tabid! <ce>
var divs = document.getElementsByTagName("div");
for (var i=divs.length-1; i>=0; i--){
  if(divs[i].style.position){
    divs[i].parentNode.removeChild(divs[i]);
  }
}
for (var i=divs.length-1; i>=0; i--){
  if(parseInt(divs[i].style.width)<600){ // <---- 任意修改
    divs[i].parentNode.removeChild(divs[i]);
  }
}
